import React from 'react';

const SliderRange = props => {
    return (
        <div className="form-group">

            <input 
                className="form-control" 
                id={props.name} 
                name={props.name} 
                type={props.inputtype} 
                required={props.required}
                {...props}
            />
            <i className={props.icon} aria-hidden="true"></i>
            <label className="ml-2">{props.title}</label>

            <div className="form-group my-5" onMouseDown={this.changeValue}>
                <input type="range" min={props.mimn} max={props.max} defaultValue={props.defaultValue} step={props.step} className="slider" id={props.idRange} />
                <div id="myMoney" className="mt-3">1.500</div> 
            </div>

        </div>
    )
}
export default SliderRange;
